mapper.select:
INSERT INTO 
	subscribe
(
	p_user_pk,
	c_user_pk
)
	VALUES
(
	#{p_user_pk},
	#{c_user_pk}
);

SELECT *
FROM subscribe
WHERE p_user_pk = #{p_user_pk} 
	AND 
	c_user_pk = #{c_user_pk}

DELETE
FROM subscribe
WHERE p_user_pk = #{p_user_pk} 
	AND 
	c_user_pk = #{c_user_pk}

SELECT COUNT(*)
FROM subscribe
WHERE p_user_pk = #{p_user_pk}
