mapper.select:
    	SELECT email, password, name, role, pk
		FROM user_info 
		WHERE email = #{email}

    	SELECT pk, name, email, role  
		FROM user_info 
		WHERE email = #{email}
		AND PASSWORD = #{password}

    	INSERT INTO 
			user_info
		(
			email, 
			password, 
			name 
		)
			VALUES
		(
			#{email}, 
			#{password}, 
			#{name}
		);
